mapper.select:
select

from flow_order
where 1=1

  and phone = #{phone,jdbcType=VARCHAR}


  and plate_number = #{plateNumber,jdbcType=VARCHAR}


  and standard_name = #{standardName,jdbcType=VARCHAR}


  and applicant_name = #{applicantName,jdbcType=VARCHAR}


  and channel_id = #{channelId,jdbcType=VARCHAR}


  and insure_status = #{insureStatus,jdbcType=VARCHAR}


  and policy_no = #{policyNo,jdbcType=VARCHAR}


  and settlement_status = #{settlementStatus,jdbcType=VARCHAR}



  and settlement_status = #{settlementNo,jdbcType=VARCHAR}



  and create_time >= #{startTime}


  and create_time <= #{endTime}



  and settlement_time >= #{settlementTime}


  and settlement_time <= #{settlementTime}
